`CreateDate`  VARCHAR(19) NULL  comment '创建时间' ,
`UpdateBy`  VARCHAR(20)  comment '更新人' ,
`UpdateName`  VARCHAR(100)  comment '更新人名称' ,
`UpdateDate`  VARCHAR(19) NULL  comment '更新时间' ,
