`JobName`  VARCHAR(200)  comment '任务名' ,
`JobCode`  VARCHAR(20)  comment '任务编号' ,
`ExecState`  VARCHAR(20)  comment '执行状态' ,
`JobGroup`  VARCHAR(20)  comment '任务分组' ,
`StartTime`  VARCHAR(19) NULL  comment '开始时间' ,
`EndTime`  VARCHAR(19) NULL  comment '结束时间' ,
`RepeatInterval`  INT(5) NULL default 0  comment '重复间隔' ,
`RepeatIntervalType`  VARCHAR(20)  comment '重复间隔类型' ,
`RepeatCount`  INT(5) NULL default 0  comment '重复次数' ,
`JobClass`  VARCHAR(100)  comment '任务实现类' ,
`ExecSql`  TEXT  comment '执行sql' ,
`RestfulApi`  VARCHAR(200)  comment 'GetUrl' ,
`JobState`  VARCHAR(30)  comment '任务状态' ,
`OrgUid`  VARCHAR(20) NULL  comment '组织' ,
`GroupUid`  VARCHAR(20) NULL  comment '集团' ,
`EnableDate`  VARCHAR(19) NULL  comment '有效开始时间' ,
`DisableDate`  VARCHAR(19) NULL  comment '有效截止时间' ,
`Dr`  TinyInt(5) NULL default 0  comment '删除标记' ,
`Ts`  BIGINT(8) NULL default 0  comment '时间戳' ,
`CreateBy`  VARCHAR(20)  comment '创建人' ,
`CreateName`  VARCHAR(100)  comment '创建人名称' ,
`CreateDate`  VARCHAR(19) NULL  comment '创建时间' ,
`UpdateBy`  VARCHAR(20)  comment '更新人' ,
`UpdateName`  VARCHAR(100)  comment '更新人名称' ,
`UpdateDate`  VARCHAR(19) NULL  comment '更新时间' ,
PRIMARY KEY (Id))comment='调度任务' ENGINE = InnoDB DEFAULT CHARSET = utf8mb4


